import React, { useEffect, useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Home.css';

const subtitles = [
  'Cybersecurity Enthusiast',
  'Full Stack Developer',
  'Open Source Contributor',
  'Problem Solver'
];

function Home() {
  const [subtitleIndex, setSubtitleIndex] = useState(0);
  const [displayed, setDisplayed] = useState('');
  const [typing, setTyping] = useState(true);
  const intervalRef = useRef();
  const canvasRef = useRef(null);

  // Matrix animation effect
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const letters = '„Ç¢„Ç°„Ç§„Ç§„Ç¶„Ç®„Ç®„Ç™„Ç´„Ç≠„ÇØ„Ç±„Ç≥„Çµ„Ç∑„Çπ„Çª„ÇΩ„Çø„ÉÅ„ÉÑ„ÉÜ„Éà„Éä„Éã„Éå„Éç„Éé0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    const fontSize = 16;
    const columns = canvas.width / fontSize;
    const drops = Array.from({ length: columns }).fill(1);

    function draw() {
      ctx.fillStyle = 'rgba(0, 0, 0, 0.05)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      ctx.fillStyle = '#0F0';
      ctx.font = `${fontSize}px monospace`;

      for (let i = 0; i < drops.length; i++) {
        const text = letters.charAt(Math.floor(Math.random() * letters.length));
        ctx.fillText(text, i * fontSize, drops[i] * fontSize);

        if (drops[i] * fontSize > canvas.height && Math.random() > 0.975) {
          drops[i] = 0;
        }

        drops[i]++;
      }

      requestAnimationFrame(draw);
    }

    draw();

    // Cleanup function
    return () => {
      // No cleanup needed for this animation
    };
  }, []); // Empty dependency array means this effect runs once on mount

  // Typing animation effect
  useEffect(() => {
    let timeout;
    if (typing) {
      if (displayed.length < subtitles[subtitleIndex].length) {
        timeout = setTimeout(() => {
          setDisplayed(subtitles[subtitleIndex].slice(0, displayed.length + 1));
        }, 60);
      } else {
        setTyping(false);
        timeout = setTimeout(() => setTyping(true), 1200);
      }
    } else {
      if (displayed.length > 0) {
        timeout = setTimeout(() => {
          setDisplayed(subtitles[subtitleIndex].slice(0, displayed.length - 1));
        }, 30);
      } else {
        setTyping(true);
        setSubtitleIndex((i) => (i + 1) % subtitles.length);
      }
    }
    return () => clearTimeout(timeout);
  }, [displayed, typing, subtitleIndex]);

  return (
    <div>
      <canvas ref={canvasRef} id="matrixCanvas"></canvas>
      <noscript>You need to enable JavaScript to run this app.</noscript>

      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <div className="hero-greeting">Hello, it&apos;s me</div>
            <div className="hero-name">Amitathoriya</div>
            <div className="hero-animated">
              And I&apos;m a <span className="animated-subtitle">{displayed}&nbsp;</span>
              <span className="cursor">|</span>
            </div>
            {/* Info Section - left-aligned, standard font */}
            <ul className="info-list">
              <li>üëã Hi, I'm @Amitathoriya</li>
              <li>üëÄ Hi! I'm Amit, I have a strong interest in cyber security, my major goals are to research the relationship between website development and cybersecurity and to design scalable, secure solutions for complex problems.</li>
              <li>üîê I recently working a Password Manager in Java, blending my interest in cybersecurity with practical technical abilities.</li>
              <li>üå± I'm currently learning coding languages (C++ and Java), bug bounty, cloud.</li>
              <li>üíûÔ∏è I'm looking to collaborate on open source projects related to cyber security.</li>
              <li>üí° I'm excited to collaborate with others on open-source projects, share my progress, and contribute.</li>
            </ul>
          </div>
          <div className="hero-image-container">
            <img src="/Git_profiel.jpg" alt="Profile" className="hero-image" />
          </div>
        </div>
      </section>

      <section className="cards-section">
        <div className="card-row">
          <Link to="/projects" className="card-link">
            <div className="card">
              <img src="/Git_profiel.jpg" alt="Projects" className="card-img" />
              <div className="card-label">Projects</div>
            </div>
          </Link>
          <Link to="/achievements" className="card-link">
            <div className="card">
              <img src="/Git_profiel.jpg" alt="Achievements" className="card-img" />
              <div className="card-label">Achievement</div>
            </div>
          </Link>
          <Link to="/about" className="card-link">
            <div className="card">
              <img src="/Git_profiel.jpg" alt="About" className="card-img" />
              <div className="card-label">About</div>
            </div>
          </Link>
        </div>
      </section>
    </div>
  );
}

export default Home;
