import React, { useEffect, useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Home.css';
import '../styles/animations.css';

// Import other page components
import About from './About';
import Skills from './Skills';
import Projects from './Projects';
import Achievements from './Achievements';
import Contact from './Contact';

const subtitles = [
  'Cybersecurity Enthusiast',
  'Full Stack Developer',
  'Open Source Contributor',
  'Problem Solver'
];

function Home() {
  const [subtitleIndex, setSubtitleIndex] = useState(0);
  const [displayed, setDisplayed] = useState('');
  const [typing, setTyping] = useState(true);
  const intervalRef = useRef();
  const canvasRef = useRef(null); // Ref for the matrix canvas

  // Matrix animation effect (runs once on mount)
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    let animationFrameId;
    let drops = [];

    const setup = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;

      const letters = 'アァイイウエエオカキクケコサシスセソタチツテトナニヌネノ0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ';
      const fontSize = 16;
      const columns = canvas.width / fontSize;
      drops = Array(Math.floor(columns)).fill(1);

      function draw() {
        ctx.fillStyle = 'rgba(0, 0, 0, 0.05)'; // Slightly transparent black for fading effect
        ctx.fillRect(0, 0, canvas.width, canvas.height);

        ctx.fillStyle = '#0F0'; // Green text
        ctx.font = `${fontSize}px monospace`;

        for (let i = 0; i < drops.length; i++) {
          const text = letters.charAt(Math.floor(Math.random() * letters.length));
          ctx.fillText(text, i * fontSize, drops[i] * fontSize);

          // Reset drop when it reaches bottom, with random chance
          if (drops[i] * fontSize > canvas.height && Math.random() > 0.975) {
            drops[i] = 0;
          }

          drops[i]++;
        }
        animationFrameId = requestAnimationFrame(draw);
      }

      draw();
    };

    setup(); // Initial setup

    // Handle resize
    const handleResize = () => {
        cancelAnimationFrame(animationFrameId);
        setup();
    };
    window.addEventListener('resize', handleResize);

    // Cleanup function
    return () => {
      cancelAnimationFrame(animationFrameId);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Typing animation effect for subtitle
  useEffect(() => {
    let timeout;
    if (typing) {
      if (displayed.length < subtitles[subtitleIndex].length) {
        timeout = setTimeout(() => {
          setDisplayed(subtitles[subtitleIndex].slice(0, displayed.length + 1));
        }, 60);
      } else {
        setTyping(false);
        timeout = setTimeout(() => setTyping(true), 1200); // Pause before deleting
      }
    } else {
      if (displayed.length > 0) {
        timeout = setTimeout(() => {
          setDisplayed(subtitles[subtitleIndex].slice(0, displayed.length - 1));
        }, 30); // Deleting speed
      } else {
        setTyping(true);
        setSubtitleIndex((i) => (i + 1) % subtitles.length); // Move to next subtitle
      }
    }
    return () => clearTimeout(timeout);
  }, [displayed, typing, subtitleIndex]);

  return (
    <div className="home-container"> {/* Main container */} 
      {/* Background Effects - Placed outside sections to cover entire page */}
      <canvas ref={canvasRef} id="matrixCanvas"></canvas>
      {/* <div className="binary-bg"></div> Removed as matrix is primary bg */}
      <noscript>You need to enable JavaScript to run this app.</noscript>

      {/* Section for sequentially loaded pages */}
      <div className="landing-pages-container">
        <div className="landing-page-section about-landing">
          <About />
        </div>
        <div className="landing-page-section skills-landing">
          <Skills />
        </div>
        <div className="landing-page-section projects-landing">
          <Projects />
        </div>
        <div className="landing-page-section achievements-landing">
          <Achievements />
        </div>
        <div className="landing-page-section contact-landing">
          <Contact />
        </div>
      </div>

      {/* Original Home Content - Appears AFTER the landing sections */}
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <div className="hero-greeting">Hello, it&apos;s me</div>
            <div className="hero-name">Amitathoriya</div>
            <div className="hero-animated">
              And I&apos;m a <span className="animated-subtitle">{displayed}</span>
              <span className="terminal-cursor"></span>
            </div>
            <ul className="info-list cyber-border">
              <li>👋 Hi, I'm @Amitathoriya</li>
              <li>👀 Hi! I'm Amit, I have a strong interest in cyber security...</li> {/* Shortened for brevity */}
              <li>🔐 I recently working a Password Manager in Java...</li>
              <li>🌱 I'm currently learning coding languages...</li>
              <li>💞️ I'm looking to collaborate on open source projects...</li>
              <li>💡 I'm excited to collaborate with others...</li>
            </ul>
          </div>
          <div className="hero-image-container cyber-border">
            <img src="/Git_profiel.jpg" alt="Profile" className="hero-image" />
          </div>
        </div>
      </section>

      <section className="cards-section">
        <div className="card-row">
          <Link to="/projects" className="card-link">
            <div className="card cyber-border">
              <img src="/Git_profiel.jpg" alt="Projects" className="card-img" />
              <div className="card-label">Projects</div>
            </div>
          </Link>
          <Link to="/achievements" className="card-link">
            <div className="card cyber-border">
              <img src="/Git_profiel.jpg" alt="Achievements" className="card-img" />
              <div className="card-label">Achievement</div>
            </div>
          </Link>
          <Link to="/about" className="card-link">
            <div className="card cyber-border">
              <img src="/Git_profiel.jpg" alt="About" className="card-img" />
              <div className="card-label">About</div>
            </div>
          </Link>
        </div>
      </section>
    </div>
  );
}

export default Home;
