import React, { useEffect, useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Home.css';

const subtitles = [
  'Cybersecurity Enthusiast',
  'Full Stack Developer',
  'Open Source Contributor',
  'Problem Solver'
];

function Home() {
  const [subtitleIndex, setSubtitleIndex] = useState(0);
  const [displayed, setDisplayed] = useState('');
  const [typing, setTyping] = useState(true);
  const intervalRef = useRef();

  useEffect(() => {
    let timeout;
    if (typing) {
      if (displayed.length < subtitles[subtitleIndex].length) {
        timeout = setTimeout(() => {
          setDisplayed(subtitles[subtitleIndex].slice(0, displayed.length + 1));
        }, 60);
      } else {
        setTyping(false);
        timeout = setTimeout(() => setTyping(true), 1200);
      }
    } else {
      if (displayed.length > 0) {
        timeout = setTimeout(() => {
          setDisplayed(subtitles[subtitleIndex].slice(0, displayed.length - 1));
        }, 30);
      } else {
        setTyping(true);
        setSubtitleIndex((i) => (i + 1) % subtitles.length);
      }
    }
    return () => clearTimeout(timeout);
  }, [displayed, typing, subtitleIndex]);

  return (
    <div className="home">
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <div className="hero-greeting">Hello, it&apos;s me</div>
            <div className="hero-name">Amitathoriya</div>
            <div className="hero-animated">
              And I&apos;m a <span className="animated-subtitle">{displayed}&nbsp;</span>
              <span className="cursor">|</span>
            </div>
            <ul className="hero-intro">
              <li>üëã Hi, I&apos;m <b>@Amitathoriya</b></li>
              <li>üëÄ I have a strong interest in cyber security, my major goals are to research the relationship between website development and cybersecurity and to design scalable, secure solutions for complex problems.</li>
              <li>üßë‚Äçüíª Currently working on a Password Manager in Java, blending my interest in cybersecurity with practical technical abilities.</li>
              <li>‚úÖ Learning coding languages (C++ and Java), bug bounty, cloud.</li>
              <li>üíûÔ∏è Looking to collaborate on open source projects related to cyber security.</li>
              <li>üí° Excited to collaborate with others on open-source projects, share my progress, and contribute.</li>
            </ul>
            <a className="cv-btn" href="#" download>Download CV</a>
          </div>
          <div className="hero-image-container">
            <img src="/Git_profiel.jpg" alt="Profile" className="hero-image" />
          </div>
        </div>
      </section>
      <div className="card-row">
        <Link to="/projects" className="card-link">
          <div className="card">
            <img src="/Git_profiel.jpg" alt="Projects" className="card-img" />
            <div className="card-label">Projects</div>
          </div>
        </Link>
        <Link to="/achievements" className="card-link">
          <div className="card">
            <img src="/Git_profiel.jpg" alt="Achievements" className="card-img" />
            <div className="card-label">Achievement</div>
          </div>
        </Link>
        <Link to="/about" className="card-link">
          <div className="card">
            <img src="/Git_profiel.jpg" alt="About" className="card-img" />
            <div className="card-label">About</div>
          </div>
        </Link>
      </div>
      <form className="join-form">
        <label className="form-heading">What to join as a inter in my company?</label>
        <input type="text" placeholder="Enter your details here... (Name: , Contact: @ig || @linkedin || @github...)" />
        <button type="submit">Sumit</button>
      </form>
    </div>
  );
}

export default Home;
